import axios from "axios";

export const AxiosInstance = axios.create({
    baseURL: '/',
    headers: {
        'Content-Type': 'application/json'
    }
})

export const generateLyrics = async (query) => {
    try {
        const response = await AxiosInstance.post('http://localhost:8080/ai', query);
        return response;
    } catch (error) {
        console.log(error);
    }
};

export const generateMusic = async (lyrics, genre) => {
    const options = {
        method: 'POST',
        url: 'https://apibox.erweima.ai/api/v1/generate',
        headers: {
            Authorization: 'Bearer {eb901d4a7f08d0222c57035ab92314bd}',
            'Content-Type': 'application/json',
            Accept: 'application/json'
        },
        data: {
            customMode: true,
            instrumental: true,
            prompt: 'lyrics:' + lyrics,
            style: genre,
            title: 'MinstrelSong',
            model: 'V3_5',
            callBackUrl: ''
        }
    };
    try {
        const { response } = await AxiosInstance.request(options);
        return response;
    } catch (error) {
        console.log(error);
    }
}
