import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import { useEffect, useState } from 'react';
import { Form, Container, Row, Col, FloatingLabel, Button } from 'react-bootstrap'
import { sendQuery } from '../src/Controller'
function App() {

  const [prompt, setPrompt] = useState("a song about a sad cat");
  const [genre, setGenre] = useState("Rock");
  const [lyrics, setLyrics] = useState("");

  const queryLLm = async (e, fullprompt) => {
    const query = { "query": fullprompt }
    e.preventDefault();
    const response = await sendQuery(query)
    console.log(query)
    console.log(response);
    console.log(response.data);
    let data = response.data.split(' ');
    data.shift();
    data = data.join(' ');
    setLyrics(data)
  }


  return (
    <div className='App'>

      <div className='form-wrapper'>
        <Container>
          <Form>
            <Row>
              <h1 style={{ marginBottom: '50px', textAlign: 'center' }}>
                Welcome to Minstrel
              </h1>
            </Row>
            <Row>
              <Col sm={6}>
                <Container fluid>
                  <Row>
                    <Col style={{ marginBottom: '10px' }}>
                      <h4>Enter the prompt here:</h4>
                      <Form.Control as='textarea' rows={5} value={prompt} onChange={(e) => setPrompt(e.target.value)} />
                    </Col>
                  </Row>
                  <Row>
                    <Form.Group className='mb-3' controlId='formSelect'>
                      <Form.Select value={genre} aria-label="Default select example" onChange={(e) => setGenre(e.target.value)}>
                        <option>Select Genre</option>
                        <option value="Rock">Rock</option>
                        <option value="Funk">Funk</option>
                        <option value="Jazz">Jazz</option>
                      </Form.Select>
                    </Form.Group>
                  </Row>
                  <Button variant='primary' type='submit' onClick={(e) => queryLLm(e, prompt + " In the genre of " + genre)}>
                    Generate
                  </Button>
                </Container>
              </Col>
              <Col sm={6}>
                <Container>
                  <Row>
                    <Col style={{ marginBottom: '10px' }}>
                      <Form.Control as='textarea' rows={10} value={lyrics} />
                    </Col>
                  </Row>
                </Container>
              </Col>
            </Row>
          </Form>
        </Container>
      </div >
    </div>
  );
}

export default App;
